<?phpclass Register extends CI_Controller {	function _construct() {			parent::CI_Controller();	}	function index() {		$data['title'] = "ExQuest | Register";		$data['css'] = base_url()."/css/theme.css";				$this->load->view('register_view', $data);	}	function step($i = 0) {		$data['title'] = "ExQuest | Register";		$data['css'] = base_url()."/css/theme.css";				$this->load->view('step'.$i, $data);	}}?>